        /// <summary>
        /// Portrait metadata: raw lines, crop offset, and crop height.
        /// </summary>
        public class PortraitData
        {
            public string[] Lines { get; init; } = Array.Empty<string>();
            public int CropStartRow { get; init; }
            public int CropRows { get; init; } = 19;
            public int SourceWidth { get; init; } = 40;
        }

        /// <summary>
        /// Returns cropped portrait lines ready for display, or null if no portrait exists.
        /// Lines are trimmed to displayWidth visible chars and end with ANSI reset.
        /// </summary>
        public static string[]? GetCroppedPortrait(CharacterRace race, int displayWidth)
        {
            if (!Portraits.TryGetValue(race, out var data))
                return null;

            var result = new string[data.CropRows];
            for (int i = 0; i < data.CropRows; i++)
            {
                int srcRow = data.CropStartRow + i;
                if (srcRow < data.Lines.Length)
                    result[i] = TrimToVisibleWidth(data.Lines[srcRow], displayWidth);
                else
                    result[i] = new string(' ', displayWidth) + "\x1b[0m";
            }
            return result;
        }

        /// <summary>
        /// Trim an ANSI-escaped string to maxWidth visible characters,
        /// padding with spaces if shorter, and appending ANSI reset.
        /// </summary>
        public static string TrimToVisibleWidth(string line, int maxWidth)
        {
            var sb = new StringBuilder();
            int visibleCount = 0;
            int i = 0;
            while (i < line.Length && visibleCount < maxWidth)
            {
                if (line[i] == '\x1b')
                {
                    // ANSI escape sequence — copy entirely (non-visible)
                    int start = i;
                    i++; // skip ESC
                    if (i < line.Length && line[i] == '[')
                    {
                        i++; // skip [
                        while (i < line.Length && !(line[i] >= '@' && line[i] <= '~'))
                            i++;
                        if (i < line.Length)
                            i++; // skip final byte
                    }
                    sb.Append(line, start, i - start);
                }
                else
                {
                    sb.Append(line[i]);
                    visibleCount++;
                    i++;
                }
            }
            // Pad with spaces if portrait is narrower than display area
            if (visibleCount < maxWidth)
                sb.Append(new string(' ', maxWidth - visibleCount));
            // Reset all ANSI attributes so stats panel renders cleanly
            sb.Append("\x1b[0m");
            return sb.ToString();
        }

