            "\x1b[30m\u2588\u2588\x1b[31;43m\u258c\x1b[33m\u2588\x1b[40m\u258c\x1b[30m\u2588\x1b[33m\u2590\x1b[1;46m\u258c\x1b[44m\u2584\x1b[34m\u2580\x1b[0;34;44m\u2588\x1b[40m\u258c\u2590\x1b[1;44m\u2588\x1b[0;30m\u2588\u2588\u2588\u2588\x1b[33;43m\u2588\u2588\u2588\u2588\x1b[40m\u2580\u2580\u258c\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[1;33m\u2580\x1b[43m\u2588\u2584\x1b[40m\u2584\x1b[0;30m\u2588\u2588\x1b[33m\u2580",
            "\x1b[30m\u2588\u2588\x1b[33m\u2590\x1b[43m\u2588\x1b[40m\u258c\x1b[30m\u2588\x1b[1;33;43m\u2588\x1b[34;44m\u2588\x1b[30m\u258c\x1b[0;34;44m\u2588\x1b[30;40m\u2588\u2588\x1b[34;44m\u2588\x1b[40m\u258c\x1b[30m\u2588\u2588\u2588\x1b[33m\u2580\u2580\x1b[43m\u2588\u2588\u2588\x1b[40m\u2584\u2584\x1b[43m\u2588\x1b[40m\u2580\x1b[30m\u2588\x1b[33m\u2580\u258c\x1b[30m\u2588\u2588\x1b[33m\u2580\x1b[43m\u2588\x1b[1m\u2584\x1b[40m\u2584\x1b[0;33m\u2580\x1b[1m\u2580\x1b[43m\u2588\u2588\x1b[40m\u2584",
            "\x1b[0;30m\u2588\u2588\x1b[31m\u2590\x1b[1;33;43m\u25a0\x1b[0;33;43m\u2588\x1b[30;40m\u2588\x1b[1;33;43m\u2588\x1b[34m\u25a0\x1b[0;34;44m\u2588\x1b[30;40m\u2588\u2588\x1b[34m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\x1b[31m\u25a0\u25a0\x1b[30;43m\u2580\u2580\u2580\x1b[40m\u2588\u2588\x1b[33m\u2580\u2580\u25a0\x1b[30m\u2588\u2588\u2588\u2588\x1b[33m\u2580\u2584\x1b[30m\u2588\x1b[1;33m\u2580\x1b[43m\u2588\x1b[0;33m\u2584\x1b[30;43m\u2580\u2584\x1b[33m\u2588",
