            " \x1b[30m\u2588\x1b[35m\u2590\x1b[45m\u2588\x1b[34;40m\u258c\x1b[30m\u2588\u2588\x1b[43m\u2580\x1b[37m\u2580\x1b[40m\u2580\x1b[30m\u2588\u2580\x1b[33m\u2584\u2584\x1b[1m\u2584\u2584\x1b[0;30m\u2588\u2588\u2588\x1b[1;37m\u2580\x1b[0;33m\u2584\x1b[43m\u2588\x1b[40m\u2584\x1b[30m\u2588\u2588\u2588\x1b[34m\u2590\x1b[35;45m\u2588\x1b[40m\u2584\x1b[30m\u2588\u2588\x1b[37m         ",
            " \x1b[30m\u2588\x1b[35;45m\u2588\x1b[40m\u258c\x1b[30m\u2588\u2588\u2588\x1b[33;43m\u2588\x1b[1m\u2588\u2588\u2588\u2588\u2588\x1b[0;33;43m\u2588\x1b[1m\u2588\u258c\x1b[0;33;43m\u2588\u2588\x1b[1m\u2588\u2588\u2588\u2580\x1b[0;33m\u2580\x1b[30m\u2588\u2588\u2588\u2588\x1b[34m\u2580\x1b[35;45m\u2588\x1b[40m\u2584\x1b[30m\u2588\x1b[37m         ",
            " \x1b[35;45m\u2588\x1b[40m\u2580\x1b[30m\u2588\u2588\u2588\u2588\x1b[31m\u2590\x1b[1;33;43m\u2588\u2588\u2580\u2580\x1b[40m\u258c\x1b[0;30m\u2588\x1b[33m\u2580\u2580\u2590\x1b[1;43m\u2580\u2590\u2588\u2588\x1b[0;31;43m\u2590\x1b[30;40m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35;45m\u2588\x1b[34m\u2590\x1b[30;40m\u2588\u2588\x1b[37m       ",
            " \x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[33m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[1;33m\u2580\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2590\x1b[35;45m\u2588\x1b[40m\u258c\x1b[30m\u2588\u2588\x1b[37m      ",
            " \x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35;44m\u2580\x1b[34;40m\u258c\x1b[30m\u2588\u2588\u2588\u2580\x1b[37m    ",
            " \x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2584\x1b[44m\u2588\x1b[30;40m\u2588\u2588\x1b[34m\u2584\u2584\x1b[30m\u2588\u2588\u2580\x1b[37m  ",
            " \x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35m\u2584\x1b[45m\u2588\u2588\x1b[40m\u2580\x1b[34m\u2580\x1b[30m\u2588\u2588\u2588\x1b[34m\u25a0\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2590\x1b[37m ",
            " \x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35m\u2584\x1b[45m\u2588\u2588\x1b[40m\u2580\x1b[34m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[1m\u2584\x1b[0;30m\u2588\u2588\x1b[1;47m\u2580\x1b[0;30m\u258c\u2588\u2588",
            "\x1b[37m \x1b[35m\u2580\u2580\u2580\u2580\u2584\u2584\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35m\u2584\x1b[45m\u2588\u2588\x1b[40m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[37m\u25a0\x1b[30m\u2588\u2588\u2588\x1b[1;47m\u2580\u2584\x1b[0;30m\u2588\u2588\u2588\x1b[35m\u2584",
            "\x1b[37m \x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35m\u2580\x1b[44m\u2584\x1b[30;40m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2584\x1b[35;44m\u25a0\x1b[40m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2584\x1b[44m\u2588\u2588\x1b[30;40m\u2588\u2588\u2588\x1b[37m\u2584\x1b[1;47m\u2590\x1b[0m\u2584\x1b[30m\u2588\u2588\u2588\u2588\x1b[34;44m\u2588\x1b[35m\u2580",
            "\x1b[30;40m\u2588\x1b[35;45m\u2588\u2588\u2588\x1b[40m\u2584\u2584\x1b[30m\u2588\u2588\u2588\x1b[35m\u2580\x1b[34m\u2584\x1b[30m\u2588\u2588\u2588\u2588\x1b[34m\u2584\u258c\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2584\u2580\u2580\u2580\x1b[30m\u2588\u2588\x1b[37m\u2584\x1b[1m\u2584\x1b[47m\u25a0\x1b[0;47m\u2588\u2588\x1b[1;30m\u25a0\x1b[40m\u2584\x1b[0;30m\u2588\u2588\u2588\x1b[44m\u2580",
            "\x1b[40m\u2588\x1b[35m\u2580\u2580\x1b[45m\u2588\u2588\u2588\x1b[34m\u2584\x1b[40m\u2584\u2584\u2590\x1b[44m\u2588\x1b[45m\u2580\x1b[30;40m\u2588\u2588\x1b[34;44m\u2588\u2588\x1b[30;40m\u2588\u2588\x1b[34m\u2584\u258c\x1b[30m\u2588\u2588\u2588\x1b[1;33m\u2584\u2584\u2584\u2584\u2584\x1b[0;30m\u2588\x1b[37m\u2580\x1b[47m\u2580\u2588\x1b[1;30;40m\u2588\u2588\u2580\u2580\x1b[0;30m\u2588\u2588\x1b[34m\u2590\x1b[44m\u2588",
            "\x1b[30;40m\u2588\u2588\u2588\u2588\x1b[34m\u2580\x1b[35m\u2580\x1b[44m\u25a0\u2584\x1b[34;45m\u2580\x1b[40m\u2584\x1b[30m\u2588\x1b[35m\u2580\x1b[44m\u25a0\x1b[34;40m\u2584\x1b[44m\u2588\x1b[30m\u2580\x1b[34;40m\u2584\x1b[44m\u2588\u2588\u2588\x1b[40m\u2580\x1b[30m\u2588\u2580\x1b[1;33m\u2580\u2580\u2580\x1b[43m\u2588\u2588\u2588\x1b[40m\u2584\x1b[0;30m\u2588\x1b[1m\u2580\u2580\x1b[0;30m\u2588\u2588\u2588\x1b[37m\u2584\u2584\x1b[30m\u2580\u2588",
            "\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[35m\u2580\x1b[44m\u2580\x1b[40m\u2584\x1b[34m\u2584\u2590\x1b[45m\u2580\x1b[35;44m\u2584\u2584\x1b[34m\u2588\u2588\u2588\x1b[30;40m\u2588\u2588\x1b[1;33m\u2590\x1b[43m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[0;30m\u2588\u2588\u2588\x1b[37m\u2590\x1b[1;30m\u258c\u25a0\u25a0\x1b[0;47m\u2588\x1b[40m\u2584",
            "\x1b[30m\u2588\x1b[34;45m\u2580\x1b[40m\u2584\u2584\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2580\x1b[44m\u2588\x1b[45m\u258c\x1b[35m\u2588\u2588\x1b[34;44m\u2588\u2588\x1b[40m\u2580\x1b[30m\u2588\u2588\x1b[1;33m\u2584\x1b[43m\u2584\u2584\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u258c\x1b[0;30m\u2588\u2588\u2588\x1b[1m\u2580\x1b[0m\u2584\x1b[1;30m\u2580\x1b[0m\u2584\u2580",
            "\x1b[30m\u2588\x1b[34;45m\u2580\u2580\x1b[30;40m\u2588\u2588\u2588\u2588\u2588\x1b[34;44m\u2588\x1b[40m\u2584\x1b[30m\u2588\u2588\u2588\x1b[34;45m\u2584\u2584\x1b[44m\u2588\x1b[40m\u2580\u2580\x1b[30m\u2588\u2588\u2588\x1b[33m\u2580\x1b[1;43m\u2580\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[0;33m\u258c\x1b[30m\u2588\u2588\u2588\u2588\x1b[37m\u2580\x1b[30m\u2580\u2588",
            "\u2588\x1b[35m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2580\x1b[44m\u2588\u2588\x1b[40m\u2584\x1b[30m\u2588\u2588\u2588\x1b[1;33m\u2584\u2584\x1b[0;33m\u25a0\x1b[30m\u2588\u2588\u2588\u2588\x1b[33m\u2580\x1b[1;43m\u2580\u2588\u2588\u2588\u2588\u2588\u2580\x1b[0;33;43m\u2588\x1b[30;40m\u2588\x1b[31m\u2590\x1b[41m\u2588\x1b[30;40m\u2588\u2588\u2588\u2588\u2588",
            "\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[34m\u2580\u2580\x1b[30m\u2588\u2588\x1b[1;33m\u2584\x1b[43m\u2588\u2588\u2588\x1b[40m\u2584\u2584\u2584\u2584\u2584\u2584\x1b[0;30m\u2588\x1b[33m\u2580\x1b[43m\u2580\x1b[1;31m\u25a0\u25a0\u25a0\x1b[0;33m\u2580\x1b[30m\u2588\x1b[31m\u2584\u258c\u2580\x1b[33m\u2584\u2580\x1b[31m\u2590\x1b[33;43m\u2588\u2588",
            "\x1b[30;40m\u2588\u2588\u2588\x1b[1m\u2584\x1b[0;30m\u2588\u2588\u2584\x1b[37m\u2584\x1b[30m\u2588\u2588\u2580\x1b[1;33m\u2584\u2584\x1b[43m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[40m\u2580\x1b[43m\u25a0\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\x1b[31m\u2584\u25a0\u2580\x1b[30m\u2588\x1b[31;41m\u2588\x1b[33;40m\u2580\x1b[30m\u2588\x1b[31;43m\u258c\x1b[33m\u2588\u2588",
            "\x1b[30;40m\u2588\u2588\x1b[37m\u2590\x1b[47m\u2588\x1b[1;30m\u2580\x1b[0;30;47m\u2580\x1b[37m\u2588\x1b[40m\u258c\x1b[30m\u2588\x1b[1;33m\u2590\x1b[43m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[40m\u2584\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[31m\u2584\x1b[41m\u2588\x1b[40m\u2580\x1b[30m\u2588\x1b[33m\u2584\x1b[43m\u2588\u2588\u2588",
            "\x1b[30;40m\u2588\x1b[1m\u2590\x1b[0;47m\u2588\u2588\u2588\u2588\u2588\x1b[30;40m\u2588\u2588\x1b[1;33;43m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[0;33m\u2584\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[33m\u2584\x1b[43m\u2588\u2588\x1b[41m\u2580\u2580\u2580",
            "\x1b[30;40m\u2588\x1b[37m\u2590\x1b[47m\u2588\u2588\u2588\u2588\x1b[30;40m\u2588\x1b[1m\u2584\x1b[0;31m\u2590\x1b[1;43m\u2590\x1b[41m\u2588\x1b[43m\u2584\u2584\u2584\u2584\x1b[33m\u2580\x1b[31m\u2584\u2584\u2584\x1b[33m\u2588\u258c\u2580\x1b[0;33m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[33m\u2580\u2580\x1b[31;41m\u2588\u2588\u2588\u2588",
            "\x1b[30;40m\u2588\x1b[1;47m\u258c\x1b[0;47m\u2588\u2588\u2588\x1b[40m\u258c\x1b[30m\u2588\x1b[1;47m\u258c\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[1;31m\u2580\u2580\x1b[0;33m\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[31m\u2580\u2580\u2580",
            "\x1b[30m\u2588\x1b[1;47m\u258c\x1b[40m\u2588\x1b[47m\u258c\u2584\x1b[40m\u2588\x1b[0;30m\u2588\x1b[37m\u2590\x1b[1;30m\u258c\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\x1b[37;47m\u2588\x1b[1;30;40m\u2584\u2588\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[31m\u2584\x1b[33m\u2584\x1b[41m\u2584\x1b[30;40m\u2588\u2588\x1b[31m\u2584\u2584\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588",
            "\u2588\x1b[1m\u2588\u2580\u2588\x1b[0;30m\u2588\x1b[1m\u2580\x1b[0;30m\u2588\u2588\x1b[1m\u2588\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\u2588\x1b[33m\u2584\x1b[30m\u2588\u2588\x1b[31m\u2584\u2580\x1b[33m\u2580\x1b[30m\u2588\u2588\x1b[33m\u2590\x1b[43m\u2588\x1b[30;40m\u2588\u2588\x1b[33;41m\u2580\x1b[31;40m\u258c\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588",
            "\u2588\x1b[1m\u2590\u258c\x1b[0;30m\u2588\u2588\u2588\x1b[1m\u258c\x1b[0;30m\u2588\u2588\u2588\u2588\u2588\x1b[31m\u2584\u25a0\x1b[33m\u25a0\x1b[30m\u2588\u2588\x1b[31m\u2584\x1b[43m\u2580\x1b[33m\u2588\x1b[31;40m\u258c\x1b[30m\u2588\x1b[33m\u2580\x1b[31m\u25a0\x1b[30m\u2588\x1b[31m\u2580\x1b[33m\u2584\u2584\x1b[43m\u2588\x1b[40m\u2584\x1b[30m\u2588\x1b[31m\u2580\u2580\x1b[30m\u2588\u2588\u2588\u2588\u2588\u2588\u2588",
