        {
            { CharacterClass.Magician, new PortraitData { Lines = MagicianPortrait, CropStartRow = 2, CropRows = 18, SourceWidth = 40 } },
            { CharacterClass.Assassin, new PortraitData { Lines = AssassinPortrait, CropStartRow = 2, CropRows = 18, SourceWidth = 40 } },
            { CharacterClass.Warrior, new PortraitData { Lines = WarriorPortrait, CropStartRow = 2, CropRows = 18, SourceWidth = 40 } },
